      color: "from-amber-500/10 to-amber-600/5",
      iconColor: "text-amber-600",
    },
    {
      icon: Users,
      title: "Community Trust",
      description: "Join thousands of satisfied customers who rely on our platform for safe, comfortable, and reliable transportation.",
      color: "from-rose-500/10 to-rose-600/5",
      iconColor: "text-rose-600",
    },
    {
      icon: Globe,
      title: "Wide Coverage",
      description: "Extensive network covering major cities and suburbs with competitive pricing and transparent fare structure.",
      color: "from-indigo-500/10 to-indigo-600/5",
      iconColor: "text-indigo-600",
    },
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
        delayChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: [0.21, 1.11, 0.81, 0.99],
      },
    },
  };

  return (
    <section className="py-32 px-4 sm:px-6 lg:px-8 bg-white relative overflow-hidden">
      {/* Background decoration */}
      <div className="absolute inset-0">
        <div className="absolute inset-0 bg-[linear-gradient(to_right,#8080800a_1px,transparent_1px),linear-gradient(to_bottom,#8080800a_1px,transparent_1px)] bg-[size:20px_20px]"></div>
      </div>

      <div className="max-w-7xl mx-auto relative z-10">
        <motion.div 
          className="text-center mb-20"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          <div className="inline-flex items-center gap-2 px-4 py-2 bg-primary/5 text-primary rounded-full text-sm font-semibold mb-6 border border-primary/10">
            <Zap className="w-4 h-4" />
            Why Choose RideShare Pro
          </div>

          <h2 className="text-5xl md:text-6xl lg:text-7xl font-black text-slate-900 mb-8 font-display leading-tight">
            Built for Your
            <span className="block bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent">
              Peace of Mind
            </span>
          </h2>

          <p className="text-xl md:text-2xl text-slate-600 max-w-4xl mx-auto leading-relaxed font-body">
            Experience the perfect blend of luxury, safety, and convenience with our premium rideshare platform designed around your needs.
          </p>
        </motion.div>

        <motion.div 
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, margin: "-100px" }}
        >
          {features.map((feature, index) => (
            <motion.div 
              key={index}
              variants={itemVariants}
              className="group relative"
            >
              <div className="h-full p-8 bg-white rounded-3xl border border-slate-200/60 shadow-sm hover:shadow-xl transition-all duration-500 hover:border-slate-300/60 hover:-translate-y-2">
                <div className={`w-16 h-16 bg-gradient-to-br ${feature.color} rounded-2xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform duration-300`}>
                  <feature.icon className={`w-8 h-8 ${feature.iconColor}`} />
                </div>

                <h3 className="text-2xl font-bold text-slate-900 mb-4 font-display group-hover:text-primary transition-colors duration-300">
                  {feature.title}
                </h3>

                <p className="text-slate-600 leading-relaxed font-body text-lg">
                  {feature.description}
                </p>

                <div className="absolute inset-0 bg-gradient-to-br from-primary/[0.02] to-accent/[0.02] rounded-3xl opacity-0 group-hover:opacity-100 transition-opacity duration-500 pointer-events-none"></div>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Bottom CTA section */}
        <motion.div 
          className="text-center mt-20"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
          viewport={{ once: true }}
        >
          <div className="inline-flex items-center gap-8 p-8 bg-gradient-to-r from-slate-50 to-blue-50/50 rounded-3xl border border-slate-200/60">
            <div className="flex -space-x-2">
              <div className="w-12 h-12 bg-gradient-to-r from-primary to-primary/80 rounded-full border-3 border-white flex items-center justify-center shadow-lg">
                <CheckCircle className="w-6 h-6 text-white" />
              </div>
              <div className="w-12 h-12 bg-gradient-to-r from-accent to-accent/80 rounded-full border-3 border-white flex items-center justify-center shadow-lg">
                <Heart className="w-6 h-6 text-white" />
              </div>
              <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-purple-600 rounded-full border-3 border-white flex items-center justify-center shadow-lg">
                <Star className="w-6 h-6 text-white" />
              </div>
            </div>

            <div className="text-left">
              <div className="text-2xl font-bold text-slate-900 font-display">Join 50,000+ Happy Riders</div>
              <div className="text-slate-600 font-semibold">Experience premium transportation today</div>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
}

client/src/components/ride-card.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Users, Cog, Fuel, Star, MapPin, Clock, Shield } from "lucide-react";
import { motion } from "framer-motion";
import BookingModal from "@/components/booking-modal";
import type { Ride } from "@shared/schema";

interface RideCardProps {
  ride: Ride;
}

export default function RideCard({ ride }: RideCardProps) {
  const { isAuthenticated } = useAuth();
  const [, navigate] = useLocation();
  const [showBookingModal, setShowBookingModal] = useState(false);

  const handleBookNow = () => {
    if (!isAuthenticated) {
      window.location.href = "/api/login";
      return;
    }
    setShowBookingModal(true);
  };

  const handleBookingSubmit = (formData: any) => {
    // Navigate to the booking page with the form data
    navigate(`/booking/${ride.id}`);
    setShowBookingModal(false);
  };

  const renderStars = (rating: string) => {
    const numRating = parseFloat(rating);
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <Star 
          key={i}
          className={`w-4 h-4 ${i <= numRating ? 'text-amber-400 fill-amber-400' : 'text-slate-300'}`}
        />
      );
    }
    return stars;
  };

  const getCategoryColor = (category: string) => {
    const colors = {
      luxury: "bg-gradient-to-r from-purple-600 to-purple-700",
      economy: "bg-gradient-to-r from-green-600 to-green-700",
      suv: "bg-gradient-to-r from-blue-600 to-blue-700",
      sedan: "bg-gradient-to-r from-slate-600 to-slate-700",
      sports: "bg-gradient-to-r from-red-600 to-red-700",
    };
    return colors[category.toLowerCase() as keyof typeof colors] || "bg-gradient-to-r from-slate-600 to-slate-700";
  };

  return (
    <>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        whileHover={{ y: -8 }}
        className="group"
      >
        <Card 
          variant="elevated" 
          className="overflow-hidden bg-white border border-slate-200/60 shadow-lg hover:shadow-2xl transition-all duration-500 hover:border-slate-300/60 rounded-2xl"
          data-testid={`card-ride-${ride.id}`}
        >
          {/* Image Container */}
          <div className="relative overflow-hidden">
            <div className="aspect-[4/3] overflow-hidden">
              <img 
                src={ride.imageUrl} 
                alt={`${ride.model} for ride booking`} 
                className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
              />
            </div>

            {/* Overlay gradients */}
            <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />

            {/* Category badge */}
            <div className="absolute top-4 left-4">
              <Badge className={`${getCategoryColor(ride.category)} text-white border-0 px-3 py-1 text-sm font-semibold shadow-lg`}>
                {ride.category}
              </Badge>
            </div>

            {/* Premium indicator */}
            <div className="absolute top-4 right-4">
              <div className="bg-white/95 backdrop-blur-sm rounded-full p-2 shadow-lg">
                <Shield className="w-4 h-4 text-emerald-600" />
              </div>
            </div>
          </div>

          <CardContent className="p-8">
            {/* Header Section */}
            <div className="flex justify-between items-start mb-6">
              <div className="flex-1">
                <h3 className="text-2xl font-bold text-slate-900 mb-2 font-display group-hover:text-primary transition-colors duration-300">
                  {ride.model}
                </h3>
                <p className="text-slate-600 font-semibold">
                  {ride.year} â€¢ {ride.category}
                </p>
              </div>
              <div className="text-right">
                <div className="text-3xl font-black text-primary font-display">
                  ${ride.pricePerDay}
                </div>
                <div className="text-slate-500 font-semibold">per day</div>
              </div>
            </div>

            {/* Features Grid */}
            <div className="grid grid-cols-3 gap-3 mb-6">
              <div className="flex items-center gap-2 p-3 bg-slate-50 rounded-xl transition-colors hover:bg-slate-100" data-testid={`text-seats-${ride.id}`}>
                <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center">
                  <Users className="w-4 h-4 text-blue-600" />
                </div>
                <div>
                  <div className="text-xs text-slate-500 font-medium">Seats</div>
                  <div className="text-sm font-semibold text-slate-900">{ride.seats}</div>
                </div>
              </div>

              <div className="flex items-center gap-2 p-3 bg-slate-50 rounded-xl transition-colors hover:bg-slate-100" data-testid={`text-transmission-${ride.id}`}>
                <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center">
                  <Cog className="w-4 h-4 text-purple-600" />
                </div>
                <div>
                  <div className="text-xs text-slate-500 font-medium">Trans</div>
                  <div className="text-sm font-semibold text-slate-900">{ride.transmission}</div>
                </div>
              </div>

              <div className="flex items-center gap-2 p-3 bg-slate-50 rounded-xl transition-colors hover:bg-slate-100" data-testid={`text-fuel-${ride.id}`}>
                <div className="w-8 h-8 bg-emerald-100 rounded-lg flex items-center justify-center">
                  <Fuel className="w-4 h-4 text-emerald-600" />
                </div>
                <div>
                  <div className="text-xs text-slate-500 font-medium">Fuel</div>
                  <div className="text-sm font-semibold text-slate-900">{ride.fuelType}</div>
                </div>
              </div>
            </div>

            {/* Rating and Location */}
            <div className="flex items-center justify-between mb-6">
              <div className="flex items-center gap-3">
                <div className="flex items-center gap-1">
                  {renderStars(ride.rating || "0")}
                </div>
                <div className="flex items-center gap-2">
                  <span className="text-lg font-bold text-slate-900" data-testid={`text-rating-${ride.id}`}>
                    {ride.rating}
                  </span>
                  <span className="text-sm text-slate-500">
                    ({ride.reviewCount || 0} reviews)
                  </span>
                </div>
              </div>

              <div className="flex items-center gap-2 text-emerald-600">
                <Clock className="w-4 h-4" />
                <span className="text-sm font-semibold">Available now</span>
              </div>
            </div>

            {/* Location */}
            <div className="flex items-center gap-2 text-slate-600 mb-6">
              <MapPin className="w-4 h-4" />
              <span className="text-sm font-medium">{ride.location}</span>
            </div>

            {/* Action Button */}
            <Button 
              onClick={handleBookNow}
              className="w-full h-12 bg-gradient-to-r from-primary to-accent hover:from-primary/90 hover:to-accent/90 text-white font-bold text-lg rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-[1.02]"
              data-testid={`button-book-${ride.id}`}
            >
              <span>Book Now</span>
              <div className="ml-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                â†’
              </div>
            </Button>
          </CardContent>
        </Card>
      </motion.div>

      {/* Booking Modal */}
      <BookingModal
        isOpen={showBookingModal}
        onClose={() => setShowBookingModal(false)}
        ride={ride}
        onSubmit={handleBookingSubmit}
      />
    </>
  );
}

client/src/components/search-filters.tsx.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Badge } from "@/components/ui/badge";
import { Search, Filter, X, MapPin, Calendar, DollarSign, Users, Fuel, Settings } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

interface SearchFiltersProps {
  onSearch: (filters: any) => void;
}

export default function SearchFilters({ onSearch }: SearchFiltersProps) {
  const [filters, setFilters] = useState({
    location: "",
    pickupDate: "",
    returnDate: "",
    vehicleType: "",
    priceRange: [0, 500],
    transmission: "",
    fuelType: "",
    seats: ""
  });

  const [showAdvanced, setShowAdvanced] = useState(false);
  const [activeFilters, setActiveFilters] = useState<string[]>([]);

  const handleFilterChange = (key: string, value: any) => {
    const newFilters = { ...filters, [key]: value };
    setFilters(newFilters);

    // Update active filters
    const active = Object.entries(newFilters)
      .filter(([k, v]) => {
        if (k === 'priceRange') return v[0] !== 0 || v[1] !== 500;
        return v !== "" && v !== null && v !== undefined;
      })
      .map(([k]) => k);
    setActiveFilters(active);
  };

  const clearFilter = (key: string) => {
    if (key === 'priceRange') {
      handleFilterChange(key, [0, 500]);
    } else {
      handleFilterChange(key, "");
    }
  };

  const clearAllFilters = () => {
    const resetFilters = {
      location: "",
      pickupDate: "",
      returnDate: "",
      vehicleType: "",
      priceRange: [0, 500],
      transmission: "",
      fuelType: "",
      seats: ""
    };
    setFilters(resetFilters);
    setActiveFilters([]);
    onSearch(resetFilters);
  };

  const handleSearch = () => {
    onSearch(filters);
  };

  const getFilterLabel = (key: string, value: any) => {
    switch (key) {
      case 'priceRange':
        return `$${value[0]} - $${value[1]}`;
      case 'vehicleType':
        return value;
      case 'transmission':
        return value;
      case 'fuelType':
        return value;
      case 'seats':
        return `${value} seats`;
      case 'location':
        return value;
      case 'pickupDate':
        return `Pickup: ${new Date(value).toLocaleDateString()}`;
      case 'returnDate':
        return `Return: ${new Date(value).toLocaleDateString()}`;
      default:
        return value;
    }
  };

  return (
    <div className="space-y-6">
      {/* Main Search Card */}
      <Card className="bg-white/95 backdrop-blur-lg border-0 shadow-2xl rounded-2xl overflow-hidden">
        <CardContent className="p-8">
          <div className="flex items-center gap-3 mb-6">
            <div className="w-10 h-10 bg-gradient-to-r from-primary to-accent rounded-xl flex items-center justify-center">
              <Search className="w-5 h-5 text-white" />
            </div>
            <h2 className="text-2xl font-bold text-slate-900 font-display">Find Your Perfect Ride</h2>
          </div>

          {/* Primary Filters Row */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            <div className="space-y-2">
              <Label htmlFor="location" className="flex items-center gap-2 text-slate-700 font-semibold">
                <MapPin className="w-4 h-4" />
                Location
              </Label>
              <Input
                id="location"
                type="text"
                placeholder="Enter city or airport"
                value={filters.location}
                onChange={(e) => handleFilterChange("location", e.target.value)}
                className="h-12 border-slate-300 focus:border-primary focus:ring-primary/20 rounded-xl"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="pickupDate" className="flex items-center gap-2 text-slate-700 font-semibold">
                <Calendar className="w-4 h-4" />
                Pickup Date
              </Label>
              <Input
                id="pickupDate"
                type="date"
                value={filters.pickupDate}
                onChange={(e) => handleFilterChange("pickupDate", e.target.value)}
                className="h-12 border-slate-300 focus:border-primary focus:ring-primary/20 rounded-xl"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="returnDate" className="flex items-center gap-2 text-slate-700 font-semibold">
                <Calendar className="w-4 h-4" />
                Return Date
              </Label>
              <Input
                id="returnDate"
                type="date"
                value={filters.returnDate}
                onChange={(e) => handleFilterChange("returnDate", e.target.value)}
                className="h-12 border-slate-300 focus:border-primary focus:ring-primary/20 rounded-xl"
              />
            </div>

            <div className="space-y-2">
              <Label className="flex items-center gap-2 text-slate-700 font-semibold">
                Vehicle Type
              </Label>
              <Select value={filters.vehicleType} onValueChange={(value) => handleFilterChange("vehicleType", value)}>
                <SelectTrigger className="h-12 border-slate-300 focus:border-primary focus:ring-primary/20 rounded-xl">
                  <SelectValue placeholder="Any Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="economy">Economy</SelectItem>
                  <SelectItem value="compact">Compact</SelectItem>
                  <SelectItem value="suv">SUV</SelectItem>
                  <SelectItem value="luxury">Luxury</SelectItem>
                  <SelectItem value="sports">Sports</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Advanced Filters Toggle */}
          <div className="flex items-center justify-between mb-4">
            <Button
              variant="outline"
              onClick={() => setShowAdvanced(!showAdvanced)}
              className="flex items-center gap-2 h-10 border-slate-300 hover:border-primary hover:bg-primary/5 rounded-xl"
            >
              <Filter className="w-4 h-4" />
              Advanced Filters
              <motion.div
                animate={{ rotate: showAdvanced ? 180 : 0 }}
                transition={{ duration: 0.2 }}
              >
                <X className="w-4 h-4" />
              </motion.div>
            </Button>

            {activeFilters.length > 0 && (
              <Button
                variant="ghost"
                onClick={clearAllFilters}
                className="text-slate-600 hover:text-slate-800"
              >
                Clear All ({activeFilters.length})
              </Button>
            )}
          </div>

          {/* Advanced Filters */}
          <AnimatePresence>
            {showAdvanced && (
              <motion.div
                initial={{ height: 0, opacity: 0 }}
                animate={{ height: "auto", opacity: 1 }}
                exit={{ height: 0, opacity: 0 }}
                transition={{ duration: 0.3 }}
                className="overflow-hidden"
              >
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6 pt-4 border-t border-slate-200">
                  <div className="space-y-3">
                    <Label className="flex items-center gap-2 text-slate-700 font-semibold">
                      <DollarSign className="w-4 h-4" />
                      Price Range (per day)
                    </Label>
                    <div className="px-3">
                      <Slider
                        value={filters.priceRange}
                        onValueChange={(value) => handleFilterChange("priceRange", value)}
                        max={500}
                        min={0}
                        step={10}
                        className="w-full"
                      />
                      <div className="flex justify-between text-sm text-slate-600 mt-2">
                        <span>${filters.priceRange[0]}</span>
                        <span>${filters.priceRange[1]}</span>
                      </div>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label className="flex items-center gap-2 text-slate-700 font-semibold">
                      <Settings className="w-4 h-4" />
                      Transmission
                    </Label>
                    <Select value={filters.transmission} onValueChange={(value) => handleFilterChange("transmission", value)}>
                      <SelectTrigger className="h-12 border-slate-300 focus:border-primary focus:ring-primary/20 rounded-xl">
                        <SelectValue placeholder="Any Transmission" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="automatic">Automatic</SelectItem>
                        <SelectItem value="manual">Manual</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="space-y-2">
                    <Label className="flex items-center gap-2 text-slate-700 font-semibold">
                      <Fuel className="w-4 h-4" />
                      Fuel Type
                    </Label>
                    <Select value={filters.fuelType} onValueChange={(value) => handleFilterChange("fuelType", value)}>
                      <SelectTrigger className="h-12 border-slate-300 focus:border-primary focus:ring-primary/20 rounded-xl">
                        <SelectValue placeholder="Any Fuel Type" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="petrol">Petrol</SelectItem>
                        <SelectItem value="diesel">Diesel</SelectItem>
                        <SelectItem value="electric">Electric</SelectItem>
                        <SelectItem value="hybrid">Hybrid</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="space-y-2">
                    <Label className="flex items-center gap-2 text-slate-700 font-semibold">
                      <Users className="w-4 h-4" />
                      Seats
                    </Label>
                    <Select value={filters.seats} onValueChange={(value) => handleFilterChange("seats", value)}>
                      <SelectTrigger className="h-12 border-slate-300 focus:border-primary focus:ring-primary/20 rounded-xl">
                        <SelectValue placeholder="Any Seats" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="2">2 Seats</SelectItem>
                        <SelectItem value="4">4 Seats</SelectItem>
                        <SelectItem value="5">5 Seats</SelectItem>
                        <SelectItem value="7">7 Seats</SelectItem>
                        <SelectItem value="8">8+ Seats</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>

          {/* Active Filters */}
          {activeFilters.length > 0 && (
            <div className="flex flex-wrap gap-2 mb-6">
              {activeFilters.map((key) => (
                <Badge
                  key={key}
                  variant="secondary"
                  className="flex items-center gap-2 px-3 py-1 bg-primary/10 text-primary border-primary/20 rounded-full"
                >
                  {getFilterLabel(key, filters[key as keyof typeof filters])}
                  <button
                    onClick={() => clearFilter(key)}
                    className="ml-1 hover:bg-primary/20 rounded-full p-0.5 transition-colors"
                  >
                    <X className="w-3 h-3" />
                  </button>
                </Badge>
              ))}
            </div>
          )}

          {/* Search Button */}
          <Button
            onClick={handleSearch}
            className="w-full h-12 bg-gradient-to-r from-primary to-accent hover:from-primary/90 hover:to-accent/90 text-white font-bold text-lg rounded-xl shadow-lg hover:shadow-xl transition-all duration-300"
          >
